mapper.select:
select id, username, nickname, headimgurl, phone, permissions, status, create_time from user
lower(username) like concat('%',lower(#{username}),'%')
and lower(nickname) like concat('%',lower(#{nickname}),'%')
and phone like concat('%',#{phone},'%')
and status = #{status}
and create_time>=#{startTime} and create_time <=#{endTime}
and is_del = 0
ORDER BY create_time DESC
limit #{limitRows}, #{pageSize}

mapper.update:
update user
set status=#{status}
where

    id=#{id}


    username = #{username}

and is_del=0

update user
set is_del=1
where

    id=#{id}


    username = #{username}

and is_del=0
